import React from 'react';
import { SpinnerContainer } from './with-spinner.styles';
import { SpinnerOverlay } from './with-spinner.styles';

const withSpinner = (WrappedComponent, isLoading) => {
  return isLoading ? (
    <SpinnerOverlay>
      <SpinnerContainer />
    </SpinnerOverlay>
  ) : (
    <WrappedComponent />
  );
};

export default withSpinner;
