import { createSelector } from 'reselect';

const COLLECTION_ID_MAP = {
  hats: 1,
  sneakers: 2,
  jackets: 3,
  womens: 4,
  mens: 5,
};
// not a best approach but somehow makes it to work
const mapCollectionIntoArrayValues = Object.values(COLLECTION_ID_MAP);

const selectShop = (state) => state.shop;

export const selectShopCollections = createSelector(
  [selectShop],
  (shop) => shop.collections
);

export const selectCollecion = (collectionUrlParam) =>
  createSelector([selectShopCollections], (collections) =>
    collections.find(
      (collection) =>
        collection.id === mapCollectionIntoArrayValues[collectionUrlParam - 1]
    )
  );
