import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import { Outlet } from 'react-router-dom';
// import CollectionsOverview from 'src/components/collections-overview/collections-overview.component';
// import withSpinner from 'src/components/with-spinner/with-spinner.component';
import {
  convertCollectionsSnapshotToMap,
  firestore,
} from 'src/firebase/firebase.utils';
import { fetchAllCollections } from 'src/redux/shop/shop.actions';
// import CollectionPage from '../collection/collection.component';

// const CollectionsOverviewWithSpinner = withSpinner(CollectionsOverview);
// const CollectionPageWithSpinner = withSpinner(CollectionPage);

const ShopPage = () => {
  const [isLoading, setIsLoading] = useState(true);
  const dispatch = useDispatch();

  useEffect(() => {
    const collectionRef = firestore.collection('collections');
    collectionRef.onSnapshot(async (snapShot) => {
      const collectionMap = convertCollectionsSnapshotToMap(snapShot);
      await dispatch(fetchAllCollections(collectionMap));
      setIsLoading(false);
    });
  }, [dispatch]);

  return (
    <div className='shop-page'>
      <Outlet context={[isLoading]} />
    </div>
  );
};

export default ShopPage;
